RSpec.describe "/machines", type: :request do
  # This should return the minimal set of attributes required to create a valid
  # Machine. As you add validations to Machine, be sure to
  # adjust the attributes here as well.
  let(:valid_attributes) {
    attributes_for :machine
  }

  let(:invalid_attributes) {
    { :invalid => true }
  }

  # This should return the minimal set of values that should be in the headers
  # in order to pass any filters (e.g. authentication) defined in
  # MachinesController, or in your router and rack
  # middleware. Be sure to keep this updated too.
  let(:valid_headers) {
    {}
  }

  describe "GET /index" do
    it "renders a successful response" do
      Machine.create! valid_attributes
      get machines_url, headers: valid_headers, as: :json
      expect(response).to be_successful
    end
  end

  describe "GET /show" do
    it "renders a successful response" do
      machine = Machine.create! valid_attributes
      get machine_url(machine), as: :json
      expect(response).to be_successful
    end
  end

  describe "POST /create" do
    context "with valid parameters" do
      it "creates a new Machine" do
        expect {
          post machines_url,
               params: { machine: valid_attributes }, headers: valid_headers, as: :json
        }.to change(Machine, :count).by(1)
      end

      it "renders a JSON response with the new machine" do
        post machines_url,
             params: { machine: valid_attributes }, headers: valid_headers, as: :json
        expect(response).to have_http_status(:created)
        expect(response.content_type).to match(a_string_including("application/json"))
      end
    end

    context "with invalid parameters" do
      it "does not create a new Machine" do
        expect {
          post machines_url,
               params: { machine: invalid_attributes }, as: :json
        }.to change(Machine, :count).by(0)
      end

      it "renders a JSON response with errors for the new machine" do
        post machines_url,
             params: { machine: invalid_attributes }, headers: valid_headers, as: :json
        expect(response).to have_http_status(:unprocessable_entity)
        expect(response.content_type).to match(/application\/json/)
      end
    end
  end

  describe "DELETE /destroy" do
    it "destroys the requested machine" do
      machine = Machine.create! valid_attributes
      expect {
        delete machine_url(machine), headers: valid_headers, as: :json
      }.to change(Machine, :count).by(-1)
    end
  end
end
